// src/assets/js/main.js
import GLightbox from "glightbox"; // Import GLightbox

// Initiate GLightbox (make sure the selector matches your elements)
export function initGLightbox() {
	const glightbox = GLightbox({
		selector: ".glightbox", // This can be modified based on your HTML elements
	});
}

// Apply .scrolled class to the body as the page is scrolled down
export function toggleScrolled() {
	const selectBody = document.querySelector("body");
	const selectHeader = document.querySelector("#header");
	if (!selectHeader.classList.contains("scroll-up-sticky") && !selectHeader.classList.contains("sticky-top") && !selectHeader.classList.contains("fixed-top")) return;
	window.scrollY > 100 ? selectBody.classList.add("scrolled") : selectBody.classList.remove("scrolled");
}

// Mobile nav toggle
export function initMobileNavToggle() {
	const mobileNavToggleBtn = document.querySelector(".mobile-nav-toggle");

	function mobileNavToogle() {
		document.querySelector("body").classList.toggle("mobile-nav-active");
		mobileNavToggleBtn.classList.toggle("bi-list");
		mobileNavToggleBtn.classList.toggle("bi-x");
	}

	if (mobileNavToggleBtn) {
		mobileNavToggleBtn.addEventListener("click", mobileNavToogle);
	}

	// Expose mobileNavToogle so it can be used outside of this function
	return mobileNavToogle;
}

// Hide mobile nav on same-page/hash links
export function hideMobileNavOnHashLinks(mobileNavToogle) {
	document.querySelectorAll("#navmenu a").forEach((navmenu) => {
		navmenu.addEventListener("click", () => {
			if (document.querySelector(".mobile-nav-active")) {
				mobileNavToogle();
			}
		});
	});
}

// Other functions remain the same
