import "./App.css";
import Header from "./components/Header";
import Hero from "./components/Hero";
import Menu from "./components/Menu";
import React, { useEffect } from "react";
import { initializeScripts } from "./assets/js/main";
function App() {
	const [isPreloaderVisible, setIsPreloaderVisible] = useState(true); // State to control preloader visibility

	useEffect(() => {
		// Initialize external scripts
		initializeScripts();

		// Hide preloader after page load
		window.addEventListener("load", () => {
			setIsPreloaderVisible(false); // Set preloader state to false after the page loads
		});

		// Cleanup listener when the component is unmounted
		return () => {
			window.removeEventListener("load", () => {
				setIsPreloaderVisible(false);
			});
		};
	}, []);
	return (
		<>
			<Header />
			<main className="main">
				<Hero />
				<Menu />
				{/* Contact Section */}
				<section id="access" className="contact section">
					{/* Section Title */}
					<div className="section-title pb-0 mb-5 mt-2">
						<h2 className="d-flex align-items-center justify-content-center">Access</h2>
					</div>
					{/* End Section Title */}
					<div className="mb-5 pt-2 container">
						<iframe
							title="maps"
							style={{
								border: 0,
								width: "100%",
								height: 500,
								filter: "grayscale(100%) invert(92%) contrast(83%)",
							}}
							src="https://maps.google.com/maps?q=sls%20shish&t=&z=13&ie=UTF8&iwloc=&output=embed"
							frameBorder={0}
							allowFullScreen=""
							loading="lazy"
							referrerPolicy="no-referrer-when-downgrade"
						/>
					</div>
					{/* End Google Maps */}
					<div className="container">
						<div className="row gy-4">
							<div>
								<div className="d-md-flex justify-content-between align-items-start">
									<div>
										<div className="info-item d-flex">
											<i className="bi bi-geo-alt flex-shrink-0" />
											<div>
												<h3>Location</h3>
												<p>〒211-0004 Kanagawa, Kawasaki, Nakahara Ward, Shinmarukohigashi, 2 Chome−924-47 シャンティ加藤 3F</p>
											</div>
										</div>
										<div className="d-md-flex">
											<div className="info-item d-flex m-0 mt-4" style={{ marginRight: "40px !important" }}>
												<i className="bi bi-telephone flex-shrink-0" />
												<div>
													<h3>Call Us</h3>
													<a
														style={{
															color: "rgba(255, 255, 255, 0.7)",
															fontSize: 14,
														}}
														href="tel:+81444225800"
													>
														+81 44-422-5800
													</a>
												</div>
											</div>
											{/* End Info Item */}
											<div className="info-item d-flex m-0 mt-4 mb-4 ">
												<i className="bi bi-envelope flex-shrink-0" />
												<div>
													<h3>Email Us</h3>
													<p>info@example.com</p>
												</div>
											</div>
											{/* End Info Item */}
										</div>
									</div>
									<div className="info-item d-flex m-0  lh-lg">
										<div>
											<h2 style={{ fontWeight: 700 }}>Open Hours</h2>
											<p>
												<b>Monday-Thursday:</b> 2:00 PM - 2:00 AM
											</p>
											<p>
												<b>Friday</b> 2:00 PM - 4:00 AM
											</p>
											<p>
												<b>Saturday</b> 1:00 PM - 4:00 AM
											</p>
											<p>
												<b>Sunday </b> 1:00 PM - 2:00 AM
											</p>
										</div>
									</div>
								</div>
								{/* End Info Item */}
							</div>
							{/* End Contact Form */}
						</div>
					</div>
				</section>
				{/* /Contact Section */}
			</main>
			<footer id="footer" className="footer">
				<div className="container footer-top pt-0">
					<div className="social-links d-flex justify-content-center mt-4">
						<a href="https://www.instagram.com/sls_shisha/">
							<i className="bi bi-twitter-x" />
						</a>
						<a className="m-0" href="https://www.instagram.com/sls_shisha/">
							<i className="bi bi-instagram" />
						</a>
					</div>
				</div>
				<div className="container copyright text-center mt-4">
					<p>
						© <span>Copyright</span> <strong className="px-1 sitename">SLS Shisha</strong> <span>All Rights Reserved</span>
					</p>
					<div className="credits">
						Powered by <a href="https://startanalytics.net/">startanalytics.net</a>
					</div>
				</div>
			</footer>
			{/* Preloader */}
			<div id="preloader" />
		</>
	);
}

export default App;
