// src/assets/js/main.js

// Apply .scrolled class to the body as the page is scrolled down
export function toggleScrolled() {
	const selectBody = document.querySelector("body");
	const selectHeader = document.querySelector("#header");
	if (!selectHeader.classList.contains("scroll-up-sticky") && !selectHeader.classList.contains("sticky-top") && !selectHeader.classList.contains("fixed-top")) return;
	window.scrollY > 100 ? selectBody.classList.add("scrolled") : selectBody.classList.remove("scrolled");
}

// Mobile nav toggle
export function initMobileNavToggle() {
	const mobileNavToggleBtn = document.querySelector(".mobile-nav-toggle");

	function mobileNavToogle() {
		document.querySelector("body").classList.toggle("mobile-nav-active");
		mobileNavToggleBtn.classList.toggle("bi-list");
		mobileNavToggleBtn.classList.toggle("bi-x");
	}

	if (mobileNavToggleBtn) {
		mobileNavToggleBtn.addEventListener("click", mobileNavToogle);
	}
}

// Hide mobile nav on same-page/hash links
export function hideMobileNavOnHashLinks() {
	document.querySelectorAll("#navmenu a").forEach((navmenu) => {
		navmenu.addEventListener("click", () => {
			if (document.querySelector(".mobile-nav-active")) {
				toggleMobileNav();
			}
		});
	});
}

// Toggle mobile nav dropdowns
export function initMobileNavDropdowns() {
	document.querySelectorAll(".navmenu .toggle-dropdown").forEach((navmenu) => {
		navmenu.addEventListener("click", function (e) {
			e.preventDefault();
			this.parentNode.classList.toggle("active");
			this.parentNode.nextElementSibling.classList.toggle("dropdown-active");
			e.stopImmediatePropagation();
		});
	});
}

// Preloader
export function initPreloader() {
	const preloader = document.querySelector("#preloader");
	if (preloader) {
		window.addEventListener("load", () => {
			preloader.remove();
		});
	}
}

// GLightbox (Assuming the GLightbox library is properly included in the HTML)
export function initGLightbox() {
	const glightbox = GLightbox({
		selector: ".glightbox",
	});
}

// Init isotope layout and filters
export function initIsotopeLayout() {
	document.querySelectorAll(".isotope-layout").forEach(function (isotopeItem) {
		let layout = isotopeItem.getAttribute("data-layout") ?? "masonry";
		let filter = isotopeItem.getAttribute("data-default-filter") ?? "*";
		let sort = isotopeItem.getAttribute("data-sort") ?? "original-order";

		let initIsotope;
		imagesLoaded(isotopeItem.querySelector(".isotope-container"), function () {
			initIsotope = new Isotope(isotopeItem.querySelector(".isotope-container"), {
				itemSelector: ".isotope-item",
				layoutMode: layout,
				filter: filter,
				sortBy: sort,
			});
		});

		isotopeItem.querySelectorAll(".isotope-filters li").forEach(function (filters) {
			filters.addEventListener(
				"click",
				function () {
					isotopeItem.querySelector(".isotope-filters .filter-active").classList.remove("filter-active");
					this.classList.add("filter-active");
					initIsotope.arrange({
						filter: this.getAttribute("data-filter"),
					});
				},
				false
			);
		});
	});
}

// Correct scrolling position upon page load for URLs containing hash links
export function correctScrollingOnLoad() {
	window.addEventListener("load", function () {
		if (window.location.hash) {
			if (document.querySelector(window.location.hash)) {
				setTimeout(() => {
					let section = document.querySelector(window.location.hash);
					let scrollMarginTop = getComputedStyle(section).scrollMarginTop;
					window.scrollTo({
						top: section.offsetTop - parseInt(scrollMarginTop),
						behavior: "smooth",
					});
				}, 100);
			}
		}
	});
}

// Navmenu Scrollspy
export function navmenuScrollspy() {
	const navmenulinks = document.querySelectorAll(".navmenu a");

	function updateActiveLink() {
		navmenulinks.forEach((navmenulink) => {
			if (!navmenulink.hash) return;
			let section = document.querySelector(navmenulink.hash);
			if (!section) return;
			let position = window.scrollY + 200;
			if (position >= section.offsetTop && position <= section.offsetTop + section.offsetHeight) {
				document.querySelectorAll(".navmenu a.active").forEach((link) => link.classList.remove("active"));
				navmenulink.classList.add("active");
			} else {
				navmenulink.classList.remove("active");
			}
		});
	}

	window.addEventListener("load", updateActiveLink);
	document.addEventListener("scroll", updateActiveLink);
}
